import React, { useState, useRef, useEffect } from "react";
import "./styles.css";

export default function App() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);

  const chatEndRef = useRef(null);

  // Auto-scroll chat when messages update
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  const sendText = async () => {
    if (!text.trim()) return;

    // Add user message to chat
    const userMessage = {
      role: "user",
      content: text,
    };
    setMessages((m) => [...m, userMessage]);

    const messageToSend = text;
    setText("");
    setLoading(true);

    try {
      const response = await fetch("/api/chat/message", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId: "user", text: messageToSend }),
      });

      // IMPORTANT FIX: backend returns TEXT, not JSON
      const data = await response.text();

      const botMessage = {
        role: "assistant",
        content: data,
      };

      setMessages((m) => [...m, botMessage]);
    } catch (err) {
      setMessages((m) => [
        ...m,
        {
          role: "assistant",
          content: "❌ Error contacting the server. Please try again.",
        },
      ]);
    }

    setLoading(false);
  };

  // Send message on Enter key
  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendText();
  };

  return (
    <div className="app">
      <h2>AI Doctor — Chat Assistant</h2>

      {/* Chat Window */}
      <div className="chat">
        {messages.map((m, i) => (
          <div key={i} className={`message ${m.role}`}>
            {m.content}
          </div>
        ))}

        {loading && (
          <div className="message assistant typing">
            ⏳ AI Doctor is thinking...
          </div>
        )}

        <div ref={chatEndRef}></div>
      </div>

      {/* Input Box */}
      <div className="controls">
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Describe symptoms, upload reports, ask health questions..."
        />
        <button onClick={sendText}>Send</button>
      </div>
    </div>
  );
}
