import React, { useState } from "react";

export default function OcrUpload() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null); // JSON response
  const [loading, setLoading] = useState(false);

  const uploadPdf = async () => {
    if (!file) return alert("Please select a PDF file.");

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);

    try {
      const response = await fetch("/api/ocr/extract", {
        method: "POST",
        body: formData
      });

      if (!response.ok) {
        const errText = await response.text();
        throw new Error(errText || "Server error");
      }

      const data = await response.json();  // backend returns JSON
      setResult(data);

    } catch (err) {
      console.error(err);
      alert("‚ùå OCR Error: " + err.message);
    }

    setLoading(false);
  };

  return (
    <div className="ocr-box">
      <h2>üìÑ Upload Medical Report (PDF) ‚Äî Advanced AI OCR</h2>

      {/* File Dropzone */}
      <div
        className="dropzone"
        onDragOver={(e) => e.preventDefault()}
        onDrop={(e) => {
          e.preventDefault();
          setFile(e.dataTransfer.files[0]);
        }}
      >
        {file ? (
          <p>Selected: {file.name}</p>
        ) : (
          <p>Drag & drop PDF here, or click to select</p>
        )}
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setFile(e.target.files[0])}
          className="file-input"
        />
      </div>

      {/* Upload Button */}
      <button onClick={uploadPdf} disabled={loading}>
        {loading ? "Extracting OCR (Textract)..." : "Upload & Extract"}
      </button>

      {/* Show OCR Result */}
      {result && (
        <div className="ocr-result">
          <h3>üìù Extracted OCR Text</h3>
          <pre>{result.text}</pre>

          {/* Extracted fields */}
          {result.fields && Object.keys(result.fields).length > 0 && (
            <>
              <h3>üìå Extracted Medical Fields</h3>
              <div className="fields-box">
                {Object.entries(result.fields).map(([key, value], i) => (
                  <div className="field-row" key={i}>
                    <strong>{key}:</strong> {value}
                  </div>
                ))}
              </div>
            </>
          )}

          <p>üìÑ Pages Detected: {result.pages}</p>
        </div>
      )}
    </div>
  );
}
