import React, { useState } from "react";

export default function OcrUpload() {
  const [file, setFile] = useState(null);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);

  const uploadPdf = async () => {
    if (!file) return alert("Please select a PDF file.");

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);

    try {
      const response = await fetch("/api/ocr/extract", {
        method: "POST",
        body: formData
      });

      // üî• Backend now returns JSON
      const result = await response.json();

      if (result.status === "success") {
        setText(result.text);
      } else {
        setText("‚ùå OCR Error: " + result.message);
      }

    } catch (err) {
      alert("OCR failed: " + err.message);
    }

    setLoading(false);
  };

  return (
    <div className="ocr-box">
      <h2>üìÑ Upload Medical Report (PDF)</h2>

      <div
        className="dropzone"
        onDragOver={(e) => e.preventDefault()}
        onDrop={(e) => {
          e.preventDefault();
          setFile(e.dataTransfer.files[0]);
        }}
      >
        {file ? (
          <p>Selected: {file.name}</p>
        ) : (
          <p>Drag & drop PDF here, or click to select</p>
        )}
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setFile(e.target.files[0])}
          className="file-input"
        />
      </div>

      <button onClick={uploadPdf} disabled={loading}>
        {loading ? "Extracting text..." : "Upload & Extract OCR"}
      </button>

      {text && (
        <div className="ocr-result">
          <h3>Extracted Text:</h3>
          <pre>{text}</pre>
        </div>
      )}
    </div>
  );
}
